# idea on using reverse digram pairs to try and identify specific words/combos
text = ""

def count_reverse_pair_freqs(text, listTuples):
    for i in range(0,len(text)-10,2):
        for j in range(i, i+10, 2):
            if((text[i] + text[i+1]) == (text[j+1] + text[j])):
                listTuples[text[i:i+2]] = (text[i:j+2])

if __name__ == '__main__':
    tuples = {}
    count_reverse_pair_freqs(text, tuples)
    print(tuples)